package Java.test;

public class Switchcase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int num=7;
		switch (num) {
		
		case 1:
		System.out.println("case 1: value is:"+num);
		
		
		case 2:
			System.out.println("case 2: value is:"+num);
		
		case 3:
			System.out.println("case 3: value is:"+num);
		case 4:
			System.out.println("case 4: value is:"+num);
		case 5:
			System.out.println("case 5: value is:"+num);
		break;
		case 6:
			System.out.println("case 6: value is:"+num);
			
			break;			
		case 7:
			System.out.println("case 7: value is:"+num);
			
		
		case 8:
			System.out.println("case 8: value is:"+num);
		case 9:
			System.out.println("case 9: value is:"+num);
			
			
		case 10:
			System.out.println("case 10: value is:"+num);

		default :
			System.out.println("default: value is:"+num);
			
		}
	}

}
